INSERT INTO CONTENTS (ENG,VI,START_TIME,END_TIME, VOLUME_SLUG) VALUES
	/* ('Life Diary Animated - Lesson 1: My secret Santa give unexpected gift','Hoạt Hình Nhật Ký Cuộc Sống - Bài 1: Ông già Noel bí mật tặng món quà bất ngờ','00:00:00.120','00:12:57.430','life-diary-animated-1'), */
	('Hi, I\'m Celina','Xin chào, tôi là Celina','00:00:00.010','00:00:01.800','life-diary-animated-1'),
	('I wonder if you believe in Santa Claus?','Tôi tò mò là liệu bạn có tin vào ông già Noel không?','00:00:02.000','00:00:04.200','life-diary-animated-1'),
	('Especially when you receive an untitled gift on Christmas like me','Nhất là khi bạn nhận được một món quà không tên vào dịp Giáng sinh giống như tôi','00:00:04.500','00:00:08.300','life-diary-animated-1'),
	('Please like, subscribe and let\'s discover who is sending me gifts','Hãy thích, đăng ký và cùng khám phá xem ai là người đã gửi cho tôi món quà ấy nhé','00:00:08.700','00:00:12.400','life-diary-animated-1'),
	('It was on the 20th of December when I received the first present','Đó là ngày 20 tháng 12, khi tôi nhận được món quà đầu tiên','00:00:12.700','00:00:16.500','life-diary-animated-1'),
	('Someone secretly left a gift box in front of my door without the sender\'s name','Có người đã bí mật để hộp quà trước cửa nhà tôi mà không có tên người gửi','00:00:16.800','00:00:21.400','life-diary-animated-1'),
	('The gift box was wrapped up beautifully and also had a bow and a heart on top','Hộp quà được gói rất đẹp và có cả chiếc nơ và hình trái tim bên trên nữa','00:00:21.600','00:00:26.000','life-diary-animated-1'),
	('When I opened the box, It was a cute red scarf with a white pattern','Khi tôi mở chiếc hộp ra, thì đó là một cái khăn troàng màu đỏ dễ thương với họa tiết màu trắng','00:00:26.300','00:00:30.800','life-diary-animated-1'),
	('The gift made me think of Diego, my boyfriend','Món quà làm tôi nghĩ đến Diego, bạn trai tôi','00:00:31.100','00:00:34.100','life-diary-animated-1'),
	('We\'d only just met, so we\'d never celebrated Christmas together','Chúng tôi chỉ mới gặp nhau, nên chúng tôi chưa từng ăn mừng Giáng sinh cùng nhau bao giờ cả','00:00:34.600','00:00:38.000','life-diary-animated-1'),
	('Maybe he wanted to surprise me so he prepared this secret gift for me','Có lẽ anh ấy muốn tôi bất ngờ nên đã chuẩn bị món quà bí mật này cho tôi','00:00:38.100','00:00:42.300','life-diary-animated-1'),
	('I happily put the scarf around my neck and went to school','Tôi vui vẻ quàng khăn quanh cổ và đến trường','00:00:42.500','00:00:46.000','life-diary-animated-1'),
	('I quickly found Diego among the crowd since he was so tall','Tôi nhanh chóng tìm thấy Diego giữa đám đông vì anh ấy rất cao lớn','00:00:46.200','00:00:49.700','life-diary-animated-1'),
	('Hey Diego, look at me','Hey Diego, nhìn em nè','00:00:50.000','00:00:51.800','life-diary-animated-1'),
	('It\'s beautiful, isn\'t it?','Thật là xinh đẹp, đúng không?','00:00:52.100','00:00:53.750','life-diary-animated-1'),
	('It really suits you','Cái đó rất hợp với em đó','00:00:53.850','00:00:55.250','life-diary-animated-1'),
	('But don\'t forget to wear gloves','Nhưng mà đừng quên đeo găng tay chứ','00:00:55.500','00:00:57.400','life-diary-animated-1'),
	('Your fingers are all red','Các ngón tay của em đều đỏ hết lên rồi kìa','00:00:57.700','00:00:59.100','life-diary-animated-1'),
	('Hey, put on my gloves','Này, đeo găng tay của anh đi','00:00:59.500','00:01:01.500','life-diary-animated-1'),
	('His glove was too big for me','Găng tay của anh ấy quá to so với tôi','00:01:02.100','00:01:03.900','life-diary-animated-1'),
	('It was hard to keep it from coming off','Khó khăn lắm mới giữ được nó không bị tuột ra','00:01:04.200','00:01:06.090','life-diary-animated-1'),
	('But it was warm, just like how he looked after for me','Nhưng mà nó ấm áp lắm, hệt như cách anh ấy quan tâm tôi vậy','00:01:06.400','00:01:09.600','life-diary-animated-1'),
	('I didn\'t know your taste is that good','Em không hề biết là gu thẩm mỹ của anh tốt thật đấy','00:01:10.000','00:01:11.900','life-diary-animated-1'),
	('He chose exactly what I like','Anh chọn chính xác cái em thích luôn','00:01:12.300','00:01:14.400','life-diary-animated-1'),
	('Huh, I don\'t really get it, choose what?','Hả, anh thực sự chẳng hiểu gì cả, chọn cái gì cơ?','00:01:14.700','00:01:17.700','life-diary-animated-1'),
	('Aren\'t you the one who sent me this scarf?','Không phải anh là người đã gửi cho em cái khăn troàng này à?','00:01:18.000','00:01:20.210','life-diary-animated-1'),
	('Diego\'s face went blank and so did I','Mặt Diego nghệch ra, và tôi cũng vậy','00:01:20.870','00:01:23.010','life-diary-animated-1'),
	('If it wasn\'t him, it was probably Phoebe','Nếu không phải là anh ấy, vậy chắc là Phoebe rồi','00:01:23.100','00:01:26.200','life-diary-animated-1'),
	('Since childhood to adulthood, we always went to the same school, so we were very close','Từ nhỏ đến lớn, chúng tôi luôn học chung trường, nên chúng tôi rất thân thiết','00:01:26.500','00:01:32.100','life-diary-animated-1'),
	('We can tell each other everything','Chúng tôi có thể kể cho nhau mọi chuyện','00:01:32.400','00:01:34.150','life-diary-animated-1'),
	('But I never received any Christmas present from her','Nhưng tôi chưa từng nhận bất kỳ món quà Giáng sinh nào từ cô ấy cả','00:01:34.500','00:01:37.400','life-diary-animated-1'),
	('She always said, "Christmas gifts are for kids only"','Cô ấy luôn nói, "quà Giáng sinh chỉ dành cho con nít thôi"','00:01:37.800','00:01:41.800','life-diary-animated-1'),
	('Real ladies don\'t need them','Những quý cô thực thụ không cần chúng','00:01:42.000','00:01:43.600','life-diary-animated-1'),
	('I\'m not sure, but what if she changed her mind and was too shy to put her name on the gift box?','Tôi không chắc lắm, nhưng có khi cô ấy đổi ý và quá ngại ngùng nên không dám để tên lên hộp quà thì sao?','00:01:44.270','00:01:48.750','life-diary-animated-1'),
	('I said goodbye to Diego and saw Phoebe in the ladies\' restroom','Tôi nói tạm biệt Diego và nhìn thấy Phoebe trong nhà vệ sinh nữ','00:01:49.200','00:01:52.900','life-diary-animated-1'),
	('I immediately gave her a hug, showing her how excited I was about the gift','Tôi lập tức ôm chầm lấy cô ấy, thể hiện cho cô ấy biết tôi vui mừng như thế nào về món quà','00:01:53.500','00:01:57.900','life-diary-animated-1'),
	('I love the gift','Tớ thích món quà lắm','00:01:58.320','00:01:59.500','life-diary-animated-1'),
	('You\'re the best friend in the world','Cậu đúng là người bạn tuyệt nhất trên đời này','00:01:59.700','00:02:01.400','life-diary-animated-1'),
	('What the hell are you saying?','Cậu đang nói cái quái gì vậy?','00:02:02.000','00:02:03.800','life-diary-animated-1'),
	('I don\'t understand anything','Tớ chẳng hiểu gì cả','00:02:03.900','00:02:05.400','life-diary-animated-1'),
	('Huh, aren\'t you the one who gave me this towel?','Hả, cậu không phải là người tặng cho tớ cái khăn này sao?','00:02:05.900','00:02:09.100','life-diary-animated-1'),
	('Of course, Who would act like a child?','Tất nhiên rồi, ai lại làm cái trò trẻ con như thế chứ?','00:02:09.900','00:02:12.500','life-diary-animated-1'),
	('So was I mistaken again?','Vậy tôi lại nhầm lẫn nữa sao?','00:02:13.200','00:02:14.700','life-diary-animated-1'),
	('I couldn\'t stop thinking about the mysterious sender that whole day, so I couldn\'t concentrate on my studies','Cả ngày hôm đó tôi không thể nào ngừng suy nghĩ về người gửi bí ẩn, nên không thể tập trung vào việc học','00:02:15.090','00:02:20.770','life-diary-animated-1'),
	('If it was not Diego or Phoebe, it could probably be my dad','Nếu không phải là Diego hay Phoebe thì có thể chắc là bố tôi rồi','00:02:21.100','00:02:24.800','life-diary-animated-1'),
	('My father is a bridge engineer','Bố tôi là kỹ sư cầu đường','00:02:25.400','00:02:27.100','life-diary-animated-1'),
	('He\'s very passionate about his work and he\'s often away from home','Ông ấy rất nhiệt huyết với công việc của mình và thường xuyên vắng nhà','00:02:27.600','00:02:31.300','life-diary-animated-1'),
	('When winter comes, the roads are very damaged by ice and snow','Khi mùa đông đến, đường xá hỏng hóc nghiêm trọng vì băng và tuyết','00:02:31.620','00:02:35.920','life-diary-animated-1'),
	('So it has been a long time since the family has celebrated Christmas together','Nên đã lâu rồi cả nhà chưa từng ăn mừng Giáng sinh cùng nhau','00:02:36.400','00:02:40.300','life-diary-animated-1'),
	('When I got home, I immediately video call with him','Khi về đến nhà, tôi lập tức gọi video với ông','00:02:41.000','00:02:44.030','life-diary-animated-1'),
	('Hi, Dad, I miss you so much','Chào bố, con nhớ bố nhiều lắm','00:02:44.300','00:02:47.300','life-diary-animated-1'),
	('I miss you too, Celina','Bố cũng nhớ con, Celina','00:02:47.400','00:02:49.030','life-diary-animated-1'),
	('I miss your mother, too','Bố nhớ cả mẹ con nữa','00:02:49.530','00:02:51.310','life-diary-animated-1'),
	('By the way, nice scarf','Nhân tiện thì khăn troàng đẹp đấy','00:02:51.870','00:02:53.750','life-diary-animated-1'),
	('You sent it for me, don\'t you?','Bố đã gửi nó cho con, đúng không?','00:02:54.130','00:02:55.570','life-diary-animated-1'),
	('Huh, no, I didn\'t','Hả, không, không đâu','00:02:56.190','00:02:58.130','life-diary-animated-1'),
	('If you want a Christmas present, I\'ll buy it for you later, okay?','Nếu con muốn quà Giáng sinh thì bố sẽ mua cho con sau nhá?','00:02:58.300','00:03:02.100','life-diary-animated-1'),
	('Do you want teddy bears or clothes?','Con muốn gấu bông hay là quần áo mới?','00:03:02.500','00:03:05.000','life-diary-animated-1'),
	('I don\'t want presents','Con không muốn quà đâu bố ạ','00:03:05.350','00:03:06.800','life-diary-animated-1'),
	('Could you please come home to celebrate Christmas with me?','Bố có thể làm ơn về nhà mừng Giáng sinh với con không?','00:03:07.230','00:03:09.810','life-diary-animated-1'),
	('Well, about this, I\'m working, Celina','Được thôi, chuyện này, bố đang làm việc, Celina','00:03:10.390','00:03:13.570','life-diary-animated-1'),
	('I\'ll call you later, okay?','Bố sẽ gọi cho con sau nhá, được chứ?','00:03:14.010','00:03:15.310','life-diary-animated-1'),
	('Again, Dad always avoided answering when I said I want him to come home early','Lại nữa rồi, bố luôn né tránh trả lời khi tôi nói muốn ông ấy về nhà sớm','00:03:16.130','00:03:21.290','life-diary-animated-1'),
	('So this Christmas, it was just me and my mother at home','Vậy là Giáng sinh này chỉ còn tôi với mẹ ở nhà thôi','00:03:22.230','00:03:25.070','life-diary-animated-1'),
	('Well, you\'re probably wondering why I didn\'t think of my mother as the one who sent the present, right?','Có lẽ bạn sẽ thắc mắc tại sao tôi không nghĩ đến mẹ là người đã gửi quà, đúng không?','00:03:25.670','00:03:31.190','life-diary-animated-1'),
	('Simply because she only gives me cookies every year','Đơn giản là vì, năm nào bà ấy cũng chỉ tặng tôi bánh quy thôi','00:03:31.790','00:03:35.300','life-diary-animated-1'),
	('My mother is a baker and owns a pastry shop in town','Mẹ tôi là thợ làm bánh và sở hữu một cửa hàng bánh ngọt trong thị trấn','00:03:37.000','00:03:40.400','life-diary-animated-1'),
	('Her shop is not very famous','Cửa hàng của bà không nổi tiếng lắm','00:03:40.730','00:03:42.230','life-diary-animated-1'),
	('She was sad because the best seller in the store was bread instead of the beautiful and sweet cupcakes that she loved','Bà rất buồn vì món bán chạy nhất trong cửa hàng là bánh mì lạt thay vì những chiếc bánh cupcake xinh đẹp, ngọt ngào mà bà yêu thích','00:03:42.790','00:03:48.650','life-diary-animated-1'),
	('Whenever the shop had too many cakes left over, she would bring them back to me','Mỗi khi cửa hàng còn thừa quá nhiều bánh ngọt thì bà sẽ mang chúng về cho tôi','00:03:49.100','00:03:53.410','life-diary-animated-1'),
	('And those Christmas cookies were exactly unsold, so she turned them into gifts for me','Và mấy cái bánh quy Giáng sinh kia chính xác là hàng không bán được, nên bà biến chúng thành quà cho tôi đó','00:03:53.930','00:03:58.690','life-diary-animated-1'),
	('I suddenly remembered that my mother did not bring some cakes to home for a long time','Tôi chợt nhớ ra là mẹ không mang mấy chiếc bánh về nhà từ rất lâu rồi','00:03:59.100','00:04:03.390','life-diary-animated-1'),
	('Perhaps the store had become more famous and crowded, so there were no more items in stock','Có lẽ cửa hàng đã trở nên nổi tiếng, đông khách hơn, nên không còn hàng tồn kho nữa','00:04:03.700','00:04:08.300','life-diary-animated-1'),
	('Maybe that\'s why she bought me another gift this year','Biết đâu chính vì thế nên bà mới mua món quà khác cho tôi năm nay','00:04:08.500','00:04:11.290','life-diary-animated-1'),
	('When I arrived at the store, my mother greeted me with a delicious fruit cake','Khi tôi đến cửa hàng, mẹ chào đón tôi bằng một cái bánh kem trái cây ngon lành','00:04:11.350','00:04:15.870','life-diary-animated-1'),
	('The store was empty as usual in opposition to my wishes','Cửa hàng vẫn vắng như mọi khi, trái ngược với mong đợi của tôi','00:04:16.329','00:04:19.910','life-diary-animated-1'),
	('Why did you make it when you know no one would buy it?','Tại sao mẹ lại làm nó khi mà mẹ biết sẽ không có ai mua chứ?','00:04:20.690','00:04:23.110','life-diary-animated-1'),
	('But I always knew you would love to eat it','Nhưng mẹ luôn biết con sẽ thích ăn nó mà','00:04:23.410','00:04:25.850','life-diary-animated-1'),
	('Haiz, I\'ll round out','Haiz, con sẽ béo mất thôi','00:04:26.300','00:04:28.250','life-diary-animated-1'),
	('I believe that even if you are as plump as a partridge, Diego will still like you','Mẹ tin là dù con có béo như con gà rừng thì Diego vẫn sẽ thích con','00:04:28.930','00:04:34.490','life-diary-animated-1'),
	('Mom, don\'t make fun of me','Mẹ, thôi nào đừng có trêu con mà','00:04:34.990','00:04:37.090','life-diary-animated-1'),
	('So you didn\'t come here to show off that your boyfriend gave you a present?','Vậy không phải con đến đây để khoe là được bạn trai tặng quà sao?','00:04:37.630','00:04:41.150','life-diary-animated-1'),
	('It suits you very well','Nó hợp với con lắm đó','00:04:41.290','00:04:43.130','life-diary-animated-1'),
	('At this point, I really can\'t explain it anymore','Tới đây thì tôi thật sự không thể giải thích nổi nữa','00:04:43.990','00:04:47.030','life-diary-animated-1'),
	('The list of people capable of giving me gifts has been eliminated','Danh sách những người có khả năng tặng quà cho tôi đều đã bị loại bỏ','00:04:47.730','00:04:51.470','life-diary-animated-1'),
	('I told my mother about the mysterious gift','Tôi kể cho mẹ nghe về món quà bí ẩn','00:04:52.170','00:04:54.310','life-diary-animated-1'),
	('She looked very confused','Bà trông có vẻ bối rối lắm','00:04:54.750','00:04:56.330','life-diary-animated-1'),
	('I think maybe they sent it to the wrong address','Mẹ nghĩ có lẽ là người ta gửi nhầm địa chỉ con ạ','00:04:56.870','00:04:59.610','life-diary-animated-1'),
	('This explanation did have a point','Lời giải thích này cũng hợp lý đấy','00:05:00.190','00:05:02.090','life-diary-animated-1'),
	('Until the next morning, I continued to receive the same gift box','Cho đến sáng hôm sau, tôi lại tiếp tục nhận được hộp quà tương tự','00:05:02.550','00:05:06.050','life-diary-animated-1'),
	('This time, I did not open it, but left it where it was, hoping its true owner would come and get it back','Lần này, tôi không mở ra, mà để lại chỗ cũ, hy vọng, chủ nhân thật sự của nó sẽ đến lấy lại','00:05:06.550','00:05:12.800','life-diary-animated-1'),
	('I went to school and couldn\'t stop thinking about the gift box','Tôi đến trường mà không thể nào ngừng nghĩ đến hộp quà','00:05:13.310','00:05:16.370','life-diary-animated-1'),
	('What if a bad guy comes to steal it, or the neighbor\'s dog comes to bite it?','Lỡ như có kẻ xấu đến lấy trộm, hoặc chú chó nhà hàng xóm đến cắn xé nó thì sao?','00:05:16.830','00:05:21.130','life-diary-animated-1'),
	('Every time I felt anxious, my stomach immediately twists','Mỗi lần tôi cảm thấy lo lắng, thì bụng tôi lập tức quặn lên','00:05:21.470','00:05:24.950','life-diary-animated-1'),
	('I rushed into the girls\' restroom','Tôi vội vã lao vào nhà vệ sinh nữ','00:05:25.210','00:05:27.010','life-diary-animated-1'),
	('Luckily, it was almost time for school, so there was still room','May mắn là sắp vào giờ học rồi, nên vẫn còn phòng','00:05:27.510','00:05:30.810','life-diary-animated-1'),
	('Meanwhile, I heard two girls talking outside','Trong lúc đó, tôi nghe thấy có hai cô gái đang nói chuyện bên ngoài','00:05:31.310','00:05:34.810','life-diary-animated-1'),
	('Hey, did you hear the news?','Ê, cậu nghe thấy tin gì chưa?','00:05:35.570','00:05:37.470','life-diary-animated-1'),
	('What news?','Tin gì?','00:05:38.170','00:05:39.230','life-diary-animated-1'),
	('Someone saw Diego and Phoebe dating','Có người trông thấy Diego và Phoebe đang hẹn hò đấy','00:05:39.230','00:05:41.870','life-diary-animated-1'),
	('Huh, isn\'t Diego Celina\'s boyfriend?','Hả, không phải Diego là bạn trai Celina sao?','00:05:42.730','00:05:45.070','life-diary-animated-1'),
	('Phoebe and Celina are still best friends, aren\'t they?','Phoebe và Celina còn là bạn thân, đúng không','00:05:45.730','00:05:48.490','life-diary-animated-1'),
	('Who knows?','Ai mà biết được?','00:05:49.030','00:05:49.850','life-diary-animated-1'),
	('I just heard that too','Tôi cũng chỉ nghe nói thôi','00:05:50.070','00:05:51.270','life-diary-animated-1'),
