INSERT INTO CONTENTS (ENG,VI,START_TIME,END_TIME, VOLUME_SLUG) VALUES
	('Augustine and I almost took down this fake Roblox plushie smuggling empire when the gang leader suddenly turned vigilant and ordered his members to armlock us','Augustine và tôi suýt nữa đã phá vỡ đế chế buôn lậu thú nhồi bông Roblox giả khi thủ lĩnh băng nhóm bỗng nhiên cảnh giác và ra lệnh cho các thành viên khóa tay chúng tôi','00:00:00.010','00:00:08.210','your-animated-story-show-7'),
	('Pablo, you got it all wrong','Pablo, cậu hiểu sai hết rồi','00:00:08.470','00:00:10.130','your-animated-story-show-7'),
	('We\'re here to make a business deal','Chúng tôi đến đây để thỏa thuận làm ăn','00:00:10.350','00:00:11.710','your-animated-story-show-7'),
	('You don\'t fool me, you sneaky little rats','Cậu đừng có lừa tôi, lũ chuột nhắt lén lút','00:00:12.090','00:00:14.530','your-animated-story-show-7'),
	('Think you can catch me?','Cậu nghĩ cậu có thể bắt được tôi sao?','00:00:14.770','00:00:15.750','your-animated-story-show-7'),
	('I am invincible','Tôi bất khả chiến bại','00:00:16.130','00:00:17.210','your-animated-story-show-7'),
	('Muahahaha','Muahahaha','00:00:17.810','00:00:18.330','your-animated-story-show-7'),
	('Suddenly, there was a knock on the door','Đột nhiên, có tiếng gõ cửa','00:00:19.230','00:00:21.010','your-animated-story-show-7'),
	('Pizza\'s here','Pizza đã đến','00:00:21.630','00:00:22.330','your-animated-story-show-7'),
	('Please take your order','Xin vui lòng nhận đơn hàng của bạn','00:00:22.470','00:00:23.350','your-animated-story-show-7'),
	('Did any of you morons order pizza?','Có thằng nào trong các cậu gọi pizza không?','00:00:23.830','00:00:25.670','your-animated-story-show-7'),
	('Sorry, boss, I didn\'t','Xin lỗi, sếp, tôi không gọi đâu','00:00:26.230','00:00:27.990','your-animated-story-show-7'),
	('I was starving','Tôi đói quá','00:00:27.990','00:00:29.290','your-animated-story-show-7'),
	('Please could I have a bite quickly before executing these snakes?','Xin cho tôi ăn nhanh một miếng trước khi xử lý mấy tên này được không?','00:00:29.750','00:00:32.450','your-animated-story-show-7'),
	('Go get the door, you dumbo','Đi mở cửa đi, đồ ngốc','00:00:32.990','00:00:34.430','your-animated-story-show-7'),
	('You','Cậu','00:00:34.890','00:00:35.170','your-animated-story-show-7'),
	('Hands over your head','Giơ tay lên','00:00:35.510','00:00:36.430','your-animated-story-show-7'),
	('Pablo then came, hide behind the door, as it opened, and standing there was..','Pablo lúc đó đến, núp sau cánh cửa khi nó mở ra, và đứng đó là..','00:00:36.750','00:00:41.670','your-animated-story-show-7'),
	('Jane','Jane','00:00:41.670','00:00:42.030','your-animated-story-show-7'),
	('Hi, lunchtime','Chào, đến giờ ăn trưa rồi','00:00:42.510','00:00:43.910','your-animated-story-show-7'),
	('Jane then pulled up her skirt slowly, revealing her stocking','Jane từ từ kéo váy lên, lộ ra chiếc tất của mình','00:00:44.410','00:00:48.090','your-animated-story-show-7'),
	('While the gang were dumbfounded, Augustine quickly restrained the gang member, while Jane slammed the door knock on Pablo','Khi băng nhóm đang sững sờ, Augustine nhanh chóng khống chế một thành viên của băng nhóm, trong khi Jane đập cửa vào Pablo','00:00:48.230','00:00:53.710','your-animated-story-show-7'),
	('And me?','Còn tôi thì sao?','00:00:53.710','00:00:54.490','your-animated-story-show-7'),
	('I stomped onto the guy\'s foot, elbowed him in the face, and pinned him on the ground','Tôi dẫm lên chân thằng đó, khuỷu tay đập vào mặt nó, rồi giữ chặt nó xuống đất','00:00:54.810','00:00:58.690','your-animated-story-show-7'),
	('Phew, all hail Queen Jane','Phew, vạn tuế Nữ hoàng Jane','00:00:59.010','00:01:00.990','your-animated-story-show-7'),
